document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelector("header"),t=document.querySelector(".header__container"),o=document.querySelector(".burger-menu"),s=document.querySelector(".navigation");document.addEventListener("scroll",()=>{window.scrollY>e.offsetHeight?(e.classList.add("scrolled"),t.classList.add("scrolled-border")):(e.classList.remove("scrolled"),t.classList.remove("scrolled-border"))}),o.addEventListener("click",()=>{s.classList.toggle("active"),o.classList.toggle("open-menu")}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();let t=this.getAttribute("href").substring(1),r=document.getElementById(t),c=document.querySelector(".header").offsetHeight,l=r.getBoundingClientRect().top;window.scrollBy({top:l-c,behavior:"smooth"}),s.classList.contains("active")&&(s.classList.remove("active"),o.classList.remove("open-menu"))})})});