document.addEventListener("DOMContentLoaded",function(){let e=new Swiper(".swiper-reviews",{direction:"horizontal",loop:!0,slidesPerView:2,slidesPerGroup:2,spaceBetween:50,pagination:{el:".swiper-pagination-review",clickable:!0}});document.querySelectorAll(".fancybox-item").forEach(n=>{n.addEventListener("click",()=>{currentSlideIndex=e.realIndex})}),Fancybox.bind('[data-fancybox="gallery"]',{Thumbs:{autoStart:!1},on:{close:()=>{document.activeElement.blur()}}})});